package demo1;

public class SwitchDemo1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int number =100;
		
		switch(number) {
		case 10 : System.out.println("The number is 10");
		break;
		case 20 : System.out.println("The number is 20");
		break;
		case 30 : System.out.println("The number is 30");
		break;
		case 40 : System.out.println("The number is 40");
		break;
		default : System.out.println("Not a valid number");
		}

	}

}
